import React, { Component, Fragment, useState } from "react";
import { Space, Table, Tag, Button, InputNumber } from 'antd';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const { Column } = Table;

const Main = (props) => {
    const [userId, setUserId] = useState(1);
    const [userName, setUserName] = useState('Apple');
    const [data, setData] = useState([
        // {
        //     key: '1',
        //     gameId: 1,
        //     playerName: 'Red',
        // },
        // {
        //     key: '2',
        //     gameId: 4,
        //     playerName: 'Yellow',
        // },
        // {
        //     key: '3',
        //     gameId: 25,
        //     playerName: 'Blue',
        // },
        // {
        //     key: '4',
        //     gameId: 40,
        //     playerName: 'Red',
        // },
    ]);
    const [joinNewGameId, setJoinNewGameId] = useState(1);
    const [newGameNum, setNewGameNum] = useState(2);
    const navigate = useNavigate();
    const location = useLocation();

    React.useEffect(() => {
        console.log("component did mount");
        console.log(location.state);
        setUserId(location.state.usrId);
        setUserName(location.state.userName);
        const reqUrl = 'http://vcm-30741.vm.duke.edu:8080/games/' + location.state.usrId;
        axios.get(reqUrl)
            .then((res) => {
                console.log(res.data);
                const newGameList = [];
                for (let index = 0; index < res.data.games.length; index++) {
                    const newGameItem = {key: (index + 1).toString(), gameId: data.games[index].gameID, playerName: data.games[index].name};
                    newGameList.push(newGameItem);
                }
                setData(newGameList);
            })
            .catch((res) => {
                alert(res.response.data.error);
            })
        // request by user userId -> get 
    }, [])

    const handleLogOut = () => {
        navigate("/");
    }

    const handleInvolvedJoin = (record) => {
        console.log(record);
        // request playerId, then add it below
        navigate("/game", {state: {usrId: userId, playerId: 1, userName: userName}});
    }

    const onJoinIdChange = (value) => {
        setJoinNewGameId(value);
    }

    const handleJoinNewGame = (value) => {
        console.log(value);
        const reqUrl = 'http://vcm-30741.vm.duke.edu:8080/joinGame/' + location.state.usrId + '/' + joinNewGameId;
        axios.post(reqUrl)
                .then((res) => {
                    console.log(res.data);
                })
                .catch(() => {
                    alert('error');
                })

        // request server for player Id, add it below
        // navigate("/placement", {state: {usrId: userId, playerId: 1, userName: userName}});
    }

    const onNewGameIdChange = (value) => {
        console.log(value);
        setNewGameNum(value);
    }

    const handleCreateNewGame = (value) => {
        console.log(value);
        let url = 'http://vcm-30741.vm.duke.edu:8080/createGame/' + userId + '/' + newGameNum;
        // console.log(url);
        // axios.post(url)
        //         .then((res) => {
        //             console.log(res.data);
        //         })
        //         .catch(() => {
        //             alert('error');
        //         })

        // request server for player Id, add it below
        navigate("/placement", {state: {usrId: userId, playerId: 1, userName: userName}});
    }

    return (
        <div className="outside-container">
            <div className="outside-header">
                <div className="main-page">
                    <div className="main-left">
                        <div className="welcome-info">
                            <h1>Hi, {userName}</h1>
                            <Button type="primary" onClick={handleLogOut}>Log Out</Button>
                        </div>
                        <div className="game-involve-section">
                            <div className="main-title">
                                <h3>Game You are Involved</h3>
                            </div>
                            <div className="game-info">
                                <Table dataSource={data}>
                                    <Column title="Game ID" dataIndex="gameId" key="gameId" />
                                    <Column title="Player Name" dataIndex="playerName" key="playerName" />
                                    <Column
                                        title="Action"
                                        key="action"
                                        render={(record) => (
                                            <Space size="middle">
                                                <a onClick={() => handleInvolvedJoin(record)}>Join</a>
                                            </Space>
                                        )}
                                    />
                                </Table>
                            </div>
                        </div>
                    </div>
                    <div className="main-right">
                        <div className="join-game-section">
                            <div className="main-title">
                                <h3>Join a Game</h3>
                            </div>
                            <div className="prompt-input">
                                <label>Game ID: </label>
                                <InputNumber min={1} value={joinNewGameId} onChange={onJoinIdChange} />
                            </div>
                            <Button type="primary" onClick={handleJoinNewGame}>Join</Button>
                        </div>
                        <div className="new-game-section">
                            <div className="main-title"><h3>Start a New Game</h3></div>
                            <div className="prompt-input">
                                <label>PlayerNum: </label>
                                <InputNumber min={1} max={4} value={newGameNum} onChange={onNewGameIdChange} />
                            </div>
                            <Button type="primary" onClick={handleCreateNewGame}>Create Game</Button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Main;
