    const [displayJoinWait, setDisplayJoinWait] = useState(false);
    const [isCreatedSuccessDisplay, setIsCreatedSuccessDisplay] = useState(false);
    const [playerId, setPlayerId] = useState(1);
    const [gameIdCreated, setGameIdCreated] = useState(1);
    const [colorGameCreated, setColorGameCreated] = useState('');
    const navigate = useNavigate();
    const location = useLocation();

    React.useEffect(() => {
        console.log("component did mount");
        console.log(location.state);
        setUserId(location.state.usrId);
        setUserName(location.state.userName);
        // const reqUrl = 'http://vcm-30741.vm.duke.edu:8080/games/' + location.state.usrId;
        // axios.get(reqUrl)
        //     .then((res) => {
        //         console.log(res.data);
        //         const newGameList = [];
        //         for (let index = 0; index < res.data.games.length; index++) {
        //             const newGameItem = { key: (index + 1).toString(), gameId: data.games[index].gameID, playerName: data.games[index].name };
        //             newGameList.push(newGameItem);
        //         }
        //         setData(newGameList);
        //     })
        //     .catch((res) => {
        //         alert(res.response.data.error);
        //     })
        // request by user userId -> get 
    }, [])

    const handleLogOut = () => {
        navigate("/");
    }

    const handleInvolvedJoin = (record) => {
        console.log(record);
        // request playerId, then add it below
        navigate("/game", { state: { usrId: userId, playerId: 1, userName: userName } });
    }

    const onJoinIdChange = (value) => {
        setJoinNewGameId(Math.floor(value));
    }

    const handleJoinNewGame = (value) => {
        // const reqUrl = 'http://vcm-30741.vm.duke.edu:8080/joinGame/' + location.state.usrId + '/' + joinNewGameId;
        // axios.post(reqUrl)
        //     .then((res) => {
        //         console.log(res.data);
        //         if (res.data.playerID == -1) {
        //             alert('Game Not Available. Please contact your friend');
        //         } else {
        //             // Might not need to use set here!!!!!!!!!!!! define a variable below
        //             // navigate("/placement", { state: { usrId: userId, playerId: res.data.playerID, userName: userName } });
        //             setDisplayJoinWait(true);
        //         }
        //     })
        //     .catch(() => {
        //         alert('error');
        //     })

        setDisplayJoinWait(true);

        // request server for player Id, add it below
        // navigate("/placement", {state: {usrId: userId, playerId: 1, userName: userName}});
    }

    const onNewGameIdChange = (value) => {
        console.log(value);
        setNewGameNum(Math.floor(value));
    }

    const handleCreateNewGame = (value) => {
        // let url = 'http://vcm-30741.vm.duke.edu:8080/createGame/' + userId + '/' + newGameNum;
        // // console.log(url);
        // axios.post(url)
        //     .then((res) => {
        //         // Might not need to use set here!!!!!!!!!!!! define a variable below
        //         setPlayerId(res.data.playerID);
        //         setGameIdCreated(res.data.gameID);
        //         setColorGameCreated(res.data.name);
        //         setIsCreatedSuccessDisplay(true);

        //         // req ready
        //         let reqReadyUrl = 'http://vcm-30741.vm.duke.edu:8080/gameReady/';
        //         let interval = setInterval(() => {
        //             axios.post(reqReadyUrl)
        //                 .then((res) => {
        //                     // console.log(res.data);
        //                     // if (res.data.playerID == -1) {
        //                     //     alert('Game Not Available. Please contact your friend');
        //                     // } else {
        //                     //     navigate("/placement", { state: { usrId: userId, playerId: res.data.playerID, userName: userName } });
