import React, { useState, useMemo } from "react";
import { Button, List, Modal, Select, InputNumber, Tooltip, Spin, notification, message } from 'antd';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import closeButton from '../../statics/close.png';
import GameBoard from "../../components/GameBoard.js";
import Spring from '../../statics/spring.jpeg';
import Summer from '../../statics/summer.jpeg';
import Autumn from '../../statics/autumn.jpeg';
import Winter from '../../statics/winter.webp';

const Context = React.createContext({
    name: 'Default',
});

const lines = [
    // Michigan to Michigan State
    {
        "x1": 74,
        "y1": 120,
        "x2": 145,
        "y2": 64,
        "distance": 1,
        "start": "Michigan",
        "end": "Michigan State"
    },
    // Michigan to Northwestern
    {
        "x1": 74,
        "y1": 120,
        "x2": 110,
        "y2": 300,
        "distance": 3,
        "start": "Michigan",
        "end": "Northwestern"
    },
    // Michigan to Indiana
    {
        "x1": 74,
        "y1": 120,
        "x2": 160,
        "y2": 200,
        "distance": 4,
        "start": "Michigan",
        "end": "Indiana"
    },
    // Michigan State to Syracuse
    {
        "x1": 160,
        "y1": 64,
        "x2": 430,
        "y2": 44,
        "distance": 8,
        "start": "Michigan State",
        "end": "Syracuse"
    },
    // Michigan State to Ohio State
    {
        "x1": 160,
        "y1": 64,
        "x2": 260,
        "y2": 170,
        "distance": 3,
        "start": "Michigan State",
        "end": "Ohio State"
    },
    // Syracuse to Uconn
    {
        "x1": 430,
        "y1": 44,
        "x2": 580,
        "y2": 68,
        "distance": 4,
        "start": "Syracuse",
        "end": "UConn"
    },
    // Syracuse to Penn State
    {
        "x1": 430,
        "y1": 44,
        "x2": 380,
        "y2": 150,
        "distance": 4,
        "start": "Syracuse",
        "end": "Penn State"
    },
    // Uconn to Upenn
    {
        "x1": 580,
        "y1": 68,
        "x2": 500,
        "y2": 260,
        "distance": 6,
        "start": "UConn",
        "end": "UPenn"
    },
    // Penn State to Upenn
    {
        "x1": 380,
        "y1": 150,
        "x2": 500,
        "y2": 260,
        "distance": 3,
        "start": "Penn State",
        "end": "UPenn"
    },
    // Penn State to Ohio State
    {
        "x1": 380,
        "y1": 150,
        "x2": 260,
        "y2": 170,
        "distance": 2,
        "start": "Penn State",
        "end": "Ohio State"
    },
    // Ohio State to Indiana
    {
        "x1": 250,
        "y1": 170,
        "x2": 160,
        "y2": 200,
        "distance": 3,
        "start": "Ohio State",
        "end": "Indiana"
    },
    // Indiana to Northwestern
    {
        "x1": 160,
        "y1": 200,
        "x2": 110,
        "y2": 320,
        "distance": 3,
        "start": "Indiana",
        "end": "Northwestern"
    },
    // Northwestern to Illinois
    {
        "x1": 110,
        "y1": 300,
        "x2": 74,
        "y2": 415,
        "distance": 2,
        "start": "Northwestern",
        "end": "Illinois"
    },
    // Illinois to Alabama
    {
        "x1": 74,
        "y1": 400,
        "x2": 54,
        "y2": 550,
        "distance": 7,
        "start": "Illinois",
        "end": "Alabama"
    },
    // Alabama to Auburn
