import React, { Component, Fragment, useState } from "react";
import { List, Input, Button, Modal, Checkbox, Form } from 'antd';
import { NavLink, useNavigate, Navigate } from 'react-router-dom';
import { axios } from 'axios';

const Login = (props) => {
    const navigate = useNavigate();

    const onFinish = (values) => {
        console.log('Success:', values);
        const content = { "username": values.username, "password": values.password };
        axios.post('http://vcm-30741.vm.duke.edu:8080/login', content)
            .then((res) => {
                navigate("/main", {state: {usrId: res.data.userId, userName: values.username}});
            })
            .catch((res) => {
                console.log('catch error');
                console.log(res);
                alert(res.response.data.error);
            })
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    return (
        <div className="outside-container">
            <div className="outside-header">
                <div className="form-area">
                    <header>
                        <h1>RISC GAME</h1>
                        <Form
                            name="basic"
                            labelCol={{
                                span: 8,
                            }}
                            wrapperCol={{
                                span: 40,
                            }}
                            style={{
                                maxWidth: 600,
                            }}
                            initialValues={{
                                remember: true,
                            }}
                            onFinish={onFinish}
                            onFinishFailed={onFinishFailed}
                            autoComplete="off"
                        >
                            <Form.Item
                                label=""
                                name="username"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Please enter your username!',
                                    },
                                ]}
                            >
                                <Input placeholder="Username" className="username" />
                            </Form.Item>
                            <Form.Item
                                label=""
                                name="password"
                                rules={[
                                    {
                                        required: true,
                                        message: 'Please enter your password!',
                                    },
                                ]}
                            >
                                <Input.Password placeholder="Password" className="password" />
                            </Form.Item>
                            <Form.Item
                                wrapperCol={{
                                    offset: 4,
                                    span: 16,
                                }}
                            >
                                <Button type="primary" htmlType="submit">
                                    Log In
                                </Button>
                            </Form.Item>
                        </Form>
                        <p>Not a user? Register</p>
                    </header>
                </div>

            </div>
        </div>
    )
}

export default Login;
